defmodule Functions do

  def find_marker("", _window), do: {0, []}

  def find_marker(content, window) do
    content
    |> String.to_charlist()
    |> do_find_marker(0, window)
  end

  def do_find_marker(content, n, window) when n + window < length(content) do
    slice = Enum.slice(content, n..n+window-1)
    case length(Enum.uniq(slice)) != window do
      true -> do_find_marker(content, n+1, window)
      false -> {n+window, slice}
    end
  end

  def output(content, window) do
    {marker, last} = Functions.find_marker(content, window)
    IO.puts("Content #{String.slice(content, 0..30)}  marker: #{marker} last: #{last}")
  end
end

content = [
  "mjqjpqmgbljsphdztnvjfqwrcgsmlb",
