  "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw",

  # Puzzle
 ""
]

Enum.each(content, fn x -> Functions.output(x, 4) end)
IO.puts("---")
Enum.each(content, fn x -> Functions.output(x, 14) end)
